import Main from '../template/Main/Main'
import axios from 'axios'
import React,{useState, useEffect} from "react"

const baseUrl = 'http://localhost:3001/alunos'

const headerProps = {

    icon: 'list',
    title: 'Lista de Alunos',
    subtitle: 'Veja a lista de alunos cadastrado no nosso sistema'

}

const initialState = {nome: '', email: ''}

export default function AlunoLista() {

    const [aluno, setAluno] = useState(initialState)
    const [alunoList, setItens] = useState([])

    useEffect(() => {

        const getAllAlunos = async () =>{

            if(allAlunos){

                setItens(allAlunos)
            
            }

        }
        
        getAllAlunos()

    },[])

    const load = (aluno) =>{

        setAluno(aluno)

    }

    const remove = (alunoSelected) =>{

        axios.delete(`${baseUrl}/${alunoSelected.id}`).then(resp =>{

            const newList = alunoList.filter(aluno => aluno !== alunoSelected)
            setItens(newList)

        })

    }

    function renderTable(){

        return(

            <table className="table mt-4">
                <thead>

                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>E-mail</th>
                        <th>Ações</th>
                    </tr>

                </thead>
                <tbody>

                    {renderRows()}

                </tbody>
            </table>
        )

    }

    function renderRows(){

        return alunoList.map(aluno => {

            return(

                <tr key={aluno.id}>

                    <td>{aluno.id}</td>
                    <td>{aluno.nome}</td>
                    <td>{aluno.email}</td>
                    <td>
                        
                        <button className="btn btn-warning" onClick={() => load(aluno)}>
                        
                            <i className="fa fa-pencil"></i>

                        </button>
                        
                        <button className="btn btn-danger ml-2" onClick={() => remove(aluno)}>
                        
                            <i className="fa fa-trash"></i>

                        </button>
                        
                    </td>

                </tr>

            )

        })

    }

    return (

        <Main {...headerProps}>

            {renderTable()}

        </Main>


    )

}
