import axios from 'axios'
import './AlunoCadastro.css'
//import InputMask from 'react-input-mask'
import Main from '../template/Main/Main'
import React, { useState, useEffect } from "react"


const baseUrl = 'http://localhost:3001/alunos'

const headerProps = {

    icon: 'users',
    title: 'Aluno',
    subtitle: 'Cadastrar alunos - CRUD'

}

//const initialState = { nome: '', responsavel: '', sexo: '', nascimento: '', cpf: '', rg: '', serie: '', pais: '', estado: '', cidade: '', bairro: '', rua: '', n_casa: '', telefone: '', celular: '', email: '' }

const arrayTodos = [

    {
        id: 1,
        nome: "Pedro",
        cpf: "123"

    },

    {
        id: 2,
        nome: "Joao",
        cpf: "456"

    }

]

export default function AlunoCadastro() {

    async function getTodos(){

        const response = await axios.get(baseUrl)
        console.log(response)

    }

    const [alunoList, setItens] = useState([])

    useEffect(() =>{

        getTodos()

    },[])

    function renderTable() {

        return (

            <table className="table mt-4">
                <thead>

                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>CPF</th>
                        <th>Ações</th>
                    </tr>

                </thead>
                <tbody>

                    {renderRows()}

                </tbody>

            </table>

        )

    }

    function renderRows() {

        return arrayTodos.map(aluno => {

            return (

                <tr key={aluno.id}>

                    <td>{aluno.id}</td>
                    <td>{aluno.nome}</td>
                    <td>{aluno.cpf}</td>
                    <td>


                        <button className="btn btn-warning" >

                            <i className="fa fa-pencil"></i>

                        </button>

                        <button className="btn btn-danger ml-2">

                            <i className="fa fa-trash"></i>

                        </button>

                    </td>

                </tr>

            )

        })

    }

    return (

        <Main {...headerProps}>

            {renderTable()}

        </Main>

    )

}
