import { useState } from "react"

export default function Votos(props){

    const [votos, setVoto] = useState(0)

    const contaVotos = () =>{

        setVoto(votos+1)

    }

    const descontaVotos = () =>{

        setVoto(votos-1)

    }

    return(

        <div>

            {props.nome}({votos}) <button onClick={contaVotos}>+1</button> <button onClick={descontaVotos}>-1</button>

        </div>

    )

}
