import Main from "../template/Main/Main"
import axios from 'axios'
import React,{useState, useEffect} from "react"

const baseUrl = 'http://localhost:3001/users'

const headerProps = {

        icon: 'users',
        title: 'Usuarios',
        subtitle: 'Cadastrar usuarios - CRUD'

}

const initialState = {nome: '', email: ''}

export default function UserCrud() {

    const [user, setUser] = useState(initialState)
    const [userList, setItens] = useState([])

    useEffect(() => {

        const getAllUsers = async () =>{

            const allUsers = await retrieveUsers()

            if(allUsers){

                setItens(allUsers)
            
            }

        }
        
        getAllUsers()

    },[])

    const retrieveUsers = async() => {

        try{

            const response = await axios.get(baseUrl)
            return (response.data)

        }catch(err){

            console.error(err.message)

        }

    }

    const load = (user) =>{

        setUser(user)

    }

    const remove = (userSelected) =>{

        axios.delete(`${baseUrl}/${userSelected.id}`).then(resp =>{

            const newList = userList.filter(user => user !== userSelected)
            setItens(newList)

        })

    }

    const save = () => {

        if(user.nome === '' || user.email === ''){

            alert('Campo vazio')
            return

        }

        const method = user.id ? 'put':'post'
        const url = user.id ? `${baseUrl}/${user.id}`: baseUrl
        axios[method](url, user).then(resp => {

            const list = getUpadeteList(resp.data)
            setItens(list)
            clear()

        })

    }

    const getUpadeteList = (data) => {

        const list = userList.filter((user) => user.id !== data.id)
        list.unshift(data)
        return list

    }

    const clear = () => {

        setUser(initialState)

    }

    const updateField = (event) => {

        setUser({

            ...user, [event.target.name]: event.target.value

        })

    }

    function renderTable(){

        return(

            <table className="table mt-4">
                <thead>

                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>E-mail</th>
                        <th>Ações</th>
                    </tr>

                </thead>
                <tbody>

                    {renderRows()}

                </tbody>
            </table>
        )

    }

    function renderRows(){

        return userList.map(user => {

            return(

                <tr key={user.id}>

                    <td>{user.id}</td>
                    <td>{user.nome}</td>
                    <td>{user.email}</td>
                    <td>
                        
                        <button className="btn btn-warning" onClick={() => load(user)}>
                        
                            <i className="fa fa-pencil"></i>

                        </button>
                        
                        <button className="btn btn-danger ml-2" onClick={() => remove(user)}>
                        
                            <i className="fa fa-trash"></i>

                        </button>
                        
                    </td>

                </tr>

            )

        })

    }

    function renderForm() {
        
        return(

            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">

                            <label>Nome</label>
                            <input type="text" className="form-control" name='nome' value={user.nome} 
                            placeholder="Digite seu nome..." onChange={e => updateField(e)} 
                            />

                        </div>
                    </div>
                    <div className="col-12 col-md-6">
                        <div className="form-group">

                            <label>E-mail</label>
                            <input type="text" className="form-control" name='email' value={user.email}
                            placeholder="Digite seu email..." onChange={e => updateField(e)}
                            />

                        </div>
                    </div>
                </div>

                <hr/>

                <div className="row">
                    <div className="col-12 d-flex justify-content-end">
                        <button className="btn btn-primary" onClick={e => save(e)}>
                            Salvar
                        </button>
                        <button className="btn btn-secondary ml-2" onClick={e => clear(e)}>
                            Cancelar
                        </button>
                    </div>
                </div>

            </div>

        )

    }

    return(

        <Main {...headerProps}>

            {renderForm()}
            {renderTable()}

        </Main>

    )
    
}
